import React, { useState } from 'react';
import UserBar from './UserBar';
import TodoList from './TodoList';
import CreateTodo from './CreateTodo';

function App() {
    const [user, setUser] = useState('');
    const [todos, setTodos] = useState([]);

    const addTodo = (title, description) => {
        const newTodo = {
            title: title,
            description: description,
            author: user,
            dateCreated: Date.now(),
            complete: false,
            dateCompleted: null
        };
        setTodos([...todos, newTodo]);
    };

    const toggleTodo = (index) => {
        const updatedTodos = [...todos];
        updatedTodos[index].complete = !updatedTodos[index].complete;
        if (updatedTodos[index].complete) {
            updatedTodos[index].dateCompleted = Date.now();
        } else {
            updatedTodos[index].dateCompleted = null;
        }
        setTodos(updatedTodos);
    };

    // Mock login function
    const handleLogin = (username) => {
        setUser(username);
    };

    // Mock logout function
    const handleLogout = () => {
        setUser('');
    };

    return (
        <div className="App">
            <UserBar handleLogin={handleLogin} handleLogout={handleLogout} currentUser={user} />

            {user && <CreateTodo user={user} addTodo={addTodo} />}

            <TodoList todos={todos} toggleTodo={toggleTodo} />
        </div>
    );
}

export default App;
