export default function Todo({ todo, onToggleComplete }) {
    const { title, description, author, dateCreated, complete, dateCompleted } = todo;

    return (
        <div className="todo-item">
            <div className="todo-header">
                <input 
                    type="checkbox" 
                    checked={complete} 
                    onChange={() => onToggleComplete(todo)} 
                />
                <h3>{title}</h3>
            </div>
            <div>{description}</div>
            <br />
            <i>Written by <b>{author}</b> on {new Date(dateCreated).toLocaleDateString()}</i>
            {complete && dateCompleted && <div>Completed on {new Date(dateCompleted).toLocaleDateString()}</div>}
        </div>
    );
}
