export default function Todo ({ title, description, author, dateCreated, complete, dateCompleted, toggleTodo }) {
    return (
         <div>
            <h3>{title}</h3>
            <div>{description}</div>
            <br />
            <i>Written by <b>{author}</b> on {new Date(dateCreated).toLocaleDateString()}</i>
            <div>
                Completed: 
                <input type="checkbox" checked={complete} onChange={toggleTodo} />
                { complete && dateCompleted ? ` on ${new Date(dateCompleted).toLocaleDateString()}` : '' }
            </div>
        </div>    
    );
}
